import React, { Component } from 'react';
import { 
  Platform, 
  StyleSheet, 
  Text, 
  View,
  TextInput,
  TouchableOpacity,
  ScrollView } 
from 'react-native';
import { createAppContainer } from 'react-navigation';
import { createStackNavigator } from 'react-navigation-stack';
import {KeyboardAvoidingView} from 'react-native';

import FontAwesomeIcon from 'react-native-vector-icons/FontAwesome';
import { Sae } from 'react-native-textinput-effects';

const instructions = Platform.select({
  ios: 'Press Cmd+R to reload,\n' + 'Cmd+D or shake for dev menu',
  android: 'Double tap R on your keyboard to reload,\n' + 'Shake or press menu button for dev menu',
});

class StartScreen extends React.Component {
  render() {
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Welcome to Parata!</Text>

        <TouchableOpacity onPress={() => this.props.navigation.navigate('CreateGroup')} style={styles.button}>
          <Text style={styles.buttonText}>Create Group</Text>
        </TouchableOpacity>

        <TouchableOpacity onPress={() => this.props.navigation.navigate('JoinGroup')} style={styles.button}>
          <Text style={styles.buttonText}>Join Group</Text>
        </TouchableOpacity>

      </View>
    );
  }
}

class CreateGroupScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      group_code: '',
      group_name: '',
    };
  }

  componentDidMount() {
    var new_code = Math.floor(Math.random()*90000) + 10000;
    this.setState({group_code: new_code});
  }

  requestGroupCode = () => {
    alert('new code: ' + JSON.stringify(this.state.group_code));
    if (this.state.group_code) this.props.navigation.navigate('Main');
  }

  render() {
    return (
      <KeyboardAvoidingView style={styles.container} behavior="padding" enabled>
        <Sae
          label={'Group Name'}
          labelStyle={{color: '#4DD0E1'}}
          iconClass={FontAwesomeIcon}
          iconName={'pencil'}
          iconColor={'#A7A7A7'}
          inputStyle={{color: 'black'}}
          onChangeText={(group_name) => this.setState({group_name})}
        />
        <Sae
          label={'Your Name'}
          labelStyle={{color: '#4DD0E1'}}
          iconClass={FontAwesomeIcon}
          iconName={'pencil'}
          iconColor={'#A7A7A7'}
          inputStyle={{color: 'black'}}
          onChangeText={(name) => this.setState({name})}
        />
        <Sae
          label={'Home Address'}
          labelStyle={{color: '#4DD0E1'}}
          iconClass={FontAwesomeIcon}
          iconName={'pencil'}
          iconColor={'#A7A7A7'}
          inputStyle={{color: 'black'}}
          onChangeText={(name) => this.setState({name})}
          style={{marginBottom: 30}}
        />

        <TouchableOpacity onPress={this.requestGroupCode} style={styles.button}>
          <Text style={styles.buttonText}>Create Group</Text>
        </TouchableOpacity>  

      </KeyboardAvoidingView>
    );
  }
}

class JoinGroupScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      group_code: ''
    };
  }

  submitGroupCode = () => {
    if (this.state.group_code === 'ABCDE') {
      alert('valid code');
      this.props.navigation.navigate('Main');
    } else {
      alert('invalid code');
    }
  }

  render() {
    return (
      <KeyboardAvoidingView style={styles.container} behavior="padding" enabled>
        <Sae
          label={'Group Code'}
          iconClass={FontAwesomeIcon}
          labelStyle={{color: '#4DD0E1'}}
          iconName={'pencil'}
          iconColor={'#A7A7A7'}
          inputStyle={{color: 'black'}}
          onChangeText={(group_code) => this.setState({group_code})}
        />
        <Sae
          label={'Your Name'}
          labelStyle={{color: '#4DD0E1'}}
          iconClass={FontAwesomeIcon}
          iconName={'pencil'}
          iconColor={'#A7A7A7'}
          inputStyle={{color: 'black'}}
          onChangeText={(name) => this.setState({name})}
        />
        <Sae
          label={'Home Address'}
          labelStyle={{color: '#4DD0E1'}}
          iconClass={FontAwesomeIcon}
          iconName={'pencil'}
          iconColor={'#A7A7A7'}
          inputStyle={{color: 'black'}}
          onChangeText={(name) => this.setState({name})}
          style={{marginBottom: 30}}
        />

        <TouchableOpacity onPress={this.submitGroupCode} style={styles.button}>
          <Text style={styles.buttonText}>Join Group {this.state.group_code}</Text>
        </TouchableOpacity>  

      </KeyboardAvoidingView>
    );
  }
}

class MainScreen extends React.Component {
  render() {
    return (
      <View style={styles.container}>
        <Text style={styles.groupInfo}>Group Code: ABCDE{"\n"}
        Group Name: friday night w/ friends!{"\n"}
        Created: </Text>
        <ScrollView>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
          <Text style={styles.entry}>hello</Text>
        </ScrollView>
        <TouchableOpacity onPress={() => this.props.navigation.navigate('Start')} style={styles.button}>
          <Text style={styles.buttonText}>Leave Group</Text>
        </TouchableOpacity>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'stretch',
    textAlign: 'center',
    marginHorizontal: 25,
    marginTop: 60,
    marginBottom: 25
    //backgroundColor: 'purple'
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  instructions: {
    textAlign: 'center',
    color: '#333333',
    marginBottom: 5,
  },
  button: {
    backgroundColor: '#4DD0E1', 
    padding: 20,
    borderRadius: 5,
    marginTop: 10,
    marginBottom: 10,
  },
  buttonText: {
    fontSize: 20,
    textAlign: 'center',
    color: '#fff',
  },
  title: {
    paddingBottom: 16,
    textAlign: 'center',
    color: '#404d5b',
    fontSize: 20,
    fontWeight: 'bold',
    opacity: 0.8,
  },
  entry: {
    fontSize: 18,
    padding: 20,
    marginTop: 10,
    borderColor: 'black',
    borderWidth: 2,
    // backgroundColor: 'blue'
  },
  groupInfo: {
    paddingBottom: 16,
    textAlign: 'left',
    color: '#404d5b',
    fontSize: 20,
    fontWeight: 'bold',
    opacity: 0.8,
  },
});

const RootStack = createStackNavigator(
  {
    Start: {
      screen: StartScreen,
      navigationOptions: {
        title: 'Start',
        headerShown: false,
      },
    },
    JoinGroup: {
      screen: JoinGroupScreen,
      navigationOptions: {
        title: 'Join Group',
        headerStyle: {
          backgroundColor: 'white',
        },
      },
    },
    CreateGroup: {
      screen: CreateGroupScreen,
      navigationOptions: {
        title: 'Create Group',
      },
    },
    Main: {
      screen: MainScreen,
      navigationOptions: {
        title: 'Main',
        headerShown: false,
      },
    },
  },
  {
    initialRouteName: 'Main',
  }
);

const AppContainer = createAppContainer(RootStack);

export default class App extends React.Component {
  render() {
    return <AppContainer />;
  }
}
